<?php/* * BloonCrypto * Habbo R63 Post-Shuffle * Based on the work of Burak, edited by BloonCrypto Git Community. (skype: burak.karamahmut) *  * https://github.com/BurakDev/BloonProject/tree/BloonCrypto */Class Config{	public static function Get($name){		$content = file_get_contents("bloon.conf");		$content = explode("\n", $content);		foreach($content as $line => $data){			if(preg_match("/".$name."/i", $data)){				$data = str_replace($name."=", "", $data);				$data = str_replace(chr(13), "", $data);				if($data == "1"){					return true;				}else if($data == "0"){					return false;				}else{					return $data;				}			}		}	}}?>