<?php/* * BloonCrypto * Habbo R63 Post-Shuffle * Based on the work of Burak, edited by BloonCrypto Git Community. (skype: burak.karamahmut) *  * https://github.com/BurakDev/BloonProject/tree/BloonCrypto */Class Config{		private $_configFile;		private static $_instance = null;		CONST CONF_FILE = "bloon.conf";		private function _construct(){		$this->_configFile = file_get_contents( self::CONF_FILE );	}		public static function Init(){		if( self::$_instance === null )			self::$_instance = new self();		return self::$_instance;	}		private function getConfigFile(){		return $this->_configFile;	}		public static function Get($name){				$content = self::Init()->getConfigFile();				if( !is_null( $content ) ){			$content = explode("\n", $content);			foreach($content as $line => $data){				if(preg_match("/".$name."/i", $data)){					$data = str_replace($name."=", "", $data);					$data = str_replace(chr(13), "", $data);					if($data == "1"){						return true;					}else if($data == "0"){						return false;					}else{						return $data;					}					die($name." not found in ". self::CONF_FILE ." !");				}			}		}else{			die("\n\nPlease check config file isn't empty !\n\n");		}	}}?>